      DB::insert("INSERT INTO treatments (id, name, procedure) VALUES (10, '', 'Para las fiebres muy altas se realizan bebidas con las ramitas de siempre viva, es decir, tallos y hojas, tres ramitas para adultos, una o dos para niños, tres vasitos al día, por tres días ')");
      DB::insert("INSERT INTO treatments (id, name, procedure) VALUES (11, '', 'Para los pterigios se depositan nueve espigas de llantén en un frasco blanco y se ponen dos días al sol y uno al sereno, libera una líquido, llamado el espíritu de la planta, de aquí se saca una gota diaria y se aplica en el ojo ')");
      DB::insert("INSERT INTO treatments (id, name, procedure) VALUES (12, '', 'Para las crisis nerviosas o también llamadas nervios, se extrae el zumo de las flores de caléndula, poniendolas dos días al sol y uno al sereno, se exprimen y se toman tres gotas por varios días')");
      DB::insert("INSERT INTO treatments (id, name, procedure) VALUES (13, '', 'Para la acidez se utiliza la verdolaga en infusión fria, es decir, desde el día anterior se dejan las ramas de la planta en un recipiente con un litro de agua  y al otro día ya se puede usar esta agua, sin necesidad de hervirse, esto se realiza por varios días.')");
