      DB::insert("INSERT INTO treatments (id, name, procedure) VALUES (20, '', 'Para las hemorroides se prepara en infusión las dos plantas, marucha y san juanito, juntas en medio litro de agua y se toma por cucharaditas, por varios días.')");
      DB::insert("INSERT INTO treatments (id, name, procedure) VALUES (21, '', 'Se utiliza solo la raíz del ribarbo, tostándola hasta convertirla en polvo y se toma en bebidas por varias semanas tres o cuatro vasos al día para curar la anemia')");
      DB::insert("INSERT INTO treatments (id, name, procedure) VALUES (22, '', 'Para las crisis nerviosas se prepara el té en sahumerio, es decir, se quema la planta y se absorbe este humo, por nueve dias, se debe hacer en horas de la noche, antes de dormir')");
