      DB::insert("INSERT INTO treatments (id, name, procedure) VALUES (31, '', 'Para prevenir y curar la caida del cabello y su debilidad se prepara la espuma de mar en infusión con otras plantas, como la melena y cascaras de sauce, en una botella de agua, por veinte días, se toman tres tomas al día.')");
      DB::insert("INSERT INTO treatments (id, name, procedure) VALUES (32, '', 'Se preparan tres hojitas de curuba en una botella de agua en infusión, cuatro vasos en el día,  si es un niño dos hojitas, cuatro copitas por nueve días, para curar la diabetes')");
      DB::insert("INSERT INTO treatments (id, name, procedure) VALUES (33, '', 'se toma una libra del azafran de fruta, se estirpa y se extrae y se toma el jugo en ayunas, por varios días hasta ver mejoria y que haya curado la anemia')");
