      DB::insert("INSERT INTO treatments (id, name, procedure) VALUES (35, '', 'Para curar la amigdalitis se toman tres hojas de tomatera con cinco del frijol garbanzo, se ponen a hervir por cinco minutos en una botella de agua, se toman tres cucharadas si son niños menores de 7 años; si son menores de 15 años, tres cucharadas; si son adultos se toman tres copitas por nueve días.')");
