      DB::insert("INSERT INTO treatments (id, name, procedure) VALUES (37, '', 'Se debe agregar tres hojas de nacedero a la mazamorra y dárselo a las señoras que estén amamantando para aumentar la leche materna o empezar a producir, en caso de que no produzca alimento.')");
      DB::insert("INSERT INTO treatments (id, name, procedure) VALUES (38, '', 'En caso de padecer de enfermedades hepáticas se debe tomar dos hojitas de cordoncillo en infusión para niños menores de doce años, y cinco hojitas para adultos por nueve días, cuatro vasitos al día.')");
      DB::insert("INSERT INTO treatments (id, name, procedure) VALUES (39, '', 'Para bajar los niveles de colesterol se cocina una hoja de col y se licua en una botella de agua, y se toman tres cucharadas al día, por cinco días, si son niños; si son adultos tres pocillos pequeños al día')");
