      DB::insert("INSERT INTO treatments (id, name, procedure) VALUES (41, '', 'Para los granos, barros y purificar la sangre se hacen bebidas con la raíz de caléndula, lavandola muy bien, se toman por siete dias, tres copitas al día')");
      DB::insert("INSERT INTO treatments (id, name, procedure) VALUES (42, '', 'Para prevenir el caner y curarlo si no está my avanzado se preparan las hojitas de caléndula en infusión, es decir, se pone a hervir medio litro de agua y cuando hierva se depositan las hojas, cinco hojas para los adultos y para los niños dos o tres, tomándolas diariamente')");
