      DB::insert("INSERT INTO treatments (id, name, procedure) VALUES (49, '', 'Para la diabetes se utiliza el orégano dulce, tres hojas para mayores en una botella de agua en infusión, por nueve días, para niños por cinco días o los adolescentes por siete días')");
