      DB::insert("INSERT INTO treatments (id, name, procedure) VALUES (89, '', 'Para el acne y purificar la sangre se puede usar la cola de caballo en baños después del baño habitual, no tiene necesidad de hervirse, simplemente desde el día anterior se pone en un recipiente con agua remojando y al otro día ya se puede usar.')");
